package university.management.system;import com.toedter.calendar.JDateChooser;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;public class TeacherLeave extends JFrame implements ActionListener {    Choice choiceEmpId, choTime;    JDateChooser selDate;    JButton submit, cancel;    TeacherLeave() {        getContentPane().setBackground(new Color(210, 232, 252));        JLabel heading = new JLabel("Apply Leave (Teacher)");        heading.setBounds(40, 50, 300, 30);        heading.setFont(new Font("Tahoma", Font.BOLD, 20));        add(heading);        JLabel labelEmpId = new JLabel("Search by Employee ID");        labelEmpId.setBounds(60, 100, 200, 20);        labelEmpId.setFont(new Font("Tahoma", Font.PLAIN, 18));        add(labelEmpId);        choiceEmpId = new Choice();        choiceEmpId.setBounds(60, 130, 200, 20);        add(choiceEmpId);        try {            Conn c = new Conn();            ResultSet resultSet = c.statement.executeQuery("SELECT employee_id FROM teacher");            while (resultSet.next()) {                choiceEmpId.add(resultSet.getString("employee_id"));            }        } catch (Exception e) {            e.printStackTrace();        }        JLabel labelDate = new JLabel("Date");        labelDate.setBounds(60, 180, 200, 20);        labelDate.setFont(new Font("Tahoma", Font.PLAIN, 18));        add(labelDate);        selDate = new JDateChooser();        selDate.setBounds(60, 210, 200, 25);        add(selDate);        JLabel labelTime = new JLabel("Time Duration");        labelTime.setBounds(60, 260, 200, 20);        labelTime.setFont(new Font("Tahoma", Font.PLAIN, 18));        add(labelTime);        choTime = new Choice();        choTime.setBounds(60, 290, 200, 20);        choTime.add("Full Day");        choTime.add("Half Day");        add(choTime);        submit = new JButton("Submit");        submit.setBounds(60, 350, 100, 25);        submit.setBackground(Color.BLACK);        submit.setForeground(Color.WHITE);        submit.addActionListener(this);        add(submit);        cancel = new JButton("Cancel");        cancel.setBounds(200, 350, 100, 25);        cancel.setBackground(Color.BLACK);        cancel.setForeground(Color.WHITE);        cancel.addActionListener(this);        add(cancel);        setSize(500, 550);        setLocation(550, 100);        setLayout(null);        setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == submit) {            String empId = choiceEmpId.getSelectedItem();            String date = ((JTextField) selDate.getDateEditor().getUiComponent()).getText();            String time = choTime.getSelectedItem();            String Q = "INSERT INTO teacherleave (employee_id, date, duration) VALUES (?, ?, ?)";            try {                Conn c = new Conn();                var pst = c.connection.prepareStatement(Q);                pst.setString(1, empId);                pst.setString(2, date);                pst.setString(3, time);                pst.executeUpdate();                JOptionPane.showMessageDialog(null, "Leave Confirmed");                setVisible(false);            } catch (Exception ex) {                ex.printStackTrace();                JOptionPane.showMessageDialog(null, "Error: " + ex.getMessage());            }        } else {            setVisible(false);        }    }    public static void main(String[] args) {        new TeacherLeave();    }}